import { useState } from 'react'

const CourseDetails = () => {

    const [courseDetails, setCourseDetails] = useState(null)
    const [courseID, setCourseID] = useState("")
    const [editMode, setEditMode] = useState(false)
    const [editedDetails, setEditedDetails] = useState(
        {
            title: "",
            description: "",
            instructor: "",
            duration: 0,
            availableSlots: 0
        }
    )


    const handleSearch = () => {
        try {
            fetch(`/api/courses/${courseID}`)
                .then(response => {
                    if (response.ok) {
                        console.log("Course existing")
                        return response.json()
                    }
                    throw new Error("No Course found")
                })
                .then(data => {
                    setCourseDetails(data)
                    setEditedDetails(data)
                })
        }
        catch (error) {
            console.error(error)
        }
    }


    const handleEdit = () => {
        setEditMode(true)
    }


    return (
        <div>

            <h2> Course Details </h2>
            <input
                type="text"
                name='id'
                placeholder='Course ID'
                value={courseID}
                onChange={(e) => setCourseID(e.target.value)}
            />
            <button onClick={handleSearch}> Search </button>
            {courseDetails && (
                <div>
                    {
                        editMode
                            ? (
                                <div>
                                    <input
                                        type="text"
                                        value={editedDetails.title}
                                        onChange={(e) => setEditedDetails(e.target.value)}
                                    />
                                    <input
                                        type="text"
                                        value={editedDetails.description}
                                        onChange={(e) => setEditedDetails(e.target.value)}
                                    />
                                    <input
                                        type="text"
                                        value={editedDetails.instructor}
                                        onChange={(e) => setEditedDetails(e.target.value)}
                                    />
                                    <input
                                        type="number"
                                        value={editedDetails.duration}
                                        onChange={(e) => setEditedDetails(e.target.value)}
                                    />
                                    <input
                                        type="number"
                                        value={editedDetails.availableSlots}
                                        onChange={(e) => setEditedDetails(e.target.value)}
                                    />
                                    <button> Submit </button>
                                    
                                </div>
                            )

                            : (
                                <div>
                                    <p>Title: {courseDetails.title}</p>
                                    <p>Description: {courseDetails.description}</p>
                                    <p>Instructor: {courseDetails.instructor}</p>
                                    <p>Duration: {courseDetails.duration}</p>
                                    <p>Available Slots: {courseDetails.availableSlots}</p>
                                    <button onClick={handleEdit}> Edit Mode </button>
                                </div>
                            )
                    }
                    
                </div>
            )}

        </div>
    )

}

export default CourseDetails
